mapper.select:
select count(n.id)
from tb_notification n, tb_topic t
where n.tid = t.id and t.is_delete = 0 and n.target_author = #{author,jdbcType=VARCHAR}

select n.*, t.title
from tb_notification n, tb_topic t
where n.tid = t.id and t.is_delete = 0 and n.target_author = #{author,jdbcType=VARCHAR}
order by n.is_read, n.in_time desc

    limit #{start,jdbcType=INTEGER}

        ,#{limit,jdbcType=INTEGER}
